mapper.select:
SELECT

FROM interface_token a

WHERE a.id = #{id}

SELECT 

FROM interface_token a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM interface_token a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE interface_token SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
